import React from 'react';
import axios from 'axios';
import SearchBar from '../SearchBar/searchBar';

import './card.css';

class Card1 extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            location: this.props.location,
            infections: this.props.infections,
            deaths: this.props.deaths,
            hospitalisations: this.props.hospitalisations
        };
        this.findInfo = this.findInfo.bind(this)
      }
      componentDidUpdate(){
          console.log('card updating')
        this.findInfo()
      }

      findInfo = async () => {
          console.log('finding info')
          const data = await axios.get('localhost:5000/getLAdata', { params: this.props.location})
        //   console.log(data)
      }

    
    render(){
        return (
            
    <div id="card1" className="card w-25 z-index-3 m-5 border border-info shadow p-3 rounded">
        <div className="card-body">
           <h3 className="card-title mb-3">Select a local authority to find out statistics</h3>
              <SearchBar searchLocation={this.props.searchLocation} />
              <div className="container">
                  <div className="textInfo">
                    <p>Infections:{this.state.infections}</p>
                    <p>Deaths:{this.state.deaths}</p>
                    <p>Hospitalisations:{this.state.hospitalisations}</p>
                    <p>Data 4</p>
                    <p>Data 5</p>
                </div>
              </div>
         </div>            
    </div>
        )
    }
}

export default Card1;
