import React from 'react';
import axios from 'axios';
import SearchBar from '../SearchBar/searchBar';

import './card.css';

class Card1 extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            cumInfections: 0,
            cumDeaths: 0,
            newCases: 0,
            newDeaths: 0
        };
        this.findInfo = this.findInfo.bind(this)
      }
      componentDidMount(){
          console.log('card mounting')
      }

      findInfo = async (location) => {
          console.log('finding info')
          console.log(this.props.location)
          const response = await axios.get('http://localhost:5000/getLAdata', { params: {location}})
          if (response.status !== 200){
            throw Error(response.message)
        } else {
            console.log(response)
            this.setState({ cumCases: response.data.data[0].cumCasesByPublishDate, cumDeaths: response.data.data[0].cumDeaths28DaysByPublishDate, newCases: response.data.data[0].newCasesByPublishDate, newDeaths: response.data.data[0].newDeaths28DaysByPublishDate })
        }
      }

    
    render(){
        console.log('card rendering')
        return (
            
    <div id="card1" className="card w-25 z-index-3 m-5 border border-info shadow p-3 rounded">
        <div className="card-body">
           <h3 className="card-title mb-3">Select a local authority to find out statistics</h3>
              <SearchBar searchLocation={this.props.searchLocation} findInfo={this.findInfo} />
              <div className="container">
                  <div className="textInfo">
                    <p>Total infections: {this.state.cumInfections}</p>
                    <p>Total deaths: {this.state.cumDeaths}</p>
                    <p>New cases today: {this.state.newCases}</p>
                    <p>New deaths today: {this.state.newDeaths}</p>
                    <p>Data 5</p>
                </div>
              </div>
         </div>            
    </div>
        )
    }
}

export default Card1;
