import { divIcon } from 'leaflet';
import React, { createRef } from 'react';
import { MapContainer, Marker, Popup, TileLayer, GeoJSON } from "react-leaflet";
import axios from 'axios';

import Location from '../../utils/location';

import Card1 from '../Card/card';


class MyMap extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            location: 0,
            latitude: 51.5,
            longitude: 0.12,
            geoJson: ''
        }
        this.searchLocation = this.searchLocation.bind(this);
        this.clickLocation = this.clickLocation.bind(this);
        this.handleClick = this.handleClick.bind(this);
        this.returnGeoJson = this.returnGeoJson.bind(this);
        this.mapRef = createRef();
      }

      clickLocation(area){
          this.setState({
              location: area})
      }

      componentDidUpdate(){
        // this.searchLocation()
        // .then(res => this.setState({ geoJson: res }))
        // .catch(err => console.log(err))
    }
      
      searchLocation(location){
          console.log(location)
        axios.get('http://localhost:5000/location', { params: {location} })
        .then(result => {
            this.setState({
                geoJson: result.data
            })       
        })
        .catch(err => {
            console.log(err);
        })
        
      }

      handleClick() {
          const map = this.mapRef.current.leafletElement;
          map.fitBounds(this.state.latitude, this.state.longitude)
      }

      returnGeoJson(){
        let geoJsonLayer = this.state.geoJson
        return <GeoJSON style={this.countryStyle} data={geoJsonLayer} />
    }

      countryStyle = {
          fillColor: 'red'
      };
      
       
    render(){        
        return(
        <div>
    {/* <button onClick={this.handleClick}>CLICK ME</button> */}
      <MapContainer center={[this.state.latitude, this.state.longitude]} zoom={5.5}>
            <Card1 searchLocation={this.searchLocation} infections="12000" deaths="400" hospitalisations="150"/>
            <TileLayer
            url="https://api.maptiler.com/maps/uk-openzoomstack-road/256/{z}/{x}/{y}.png?key=X7qpq0iR5R376h5yMZXo" 
            attribution='<a href="https://www.maptiler.com/copyright/" target="_blank">&copy; MapTiler</a> <a href="https://www.openstreetmap.org/copyright" target="_blank">&copy; OpenStreetMap contributors</a>'
            />
            {/* <TileLayer fitBounds={[this.state.latitude, this.state.longitude]} /> */}
            <Marker position={[
                this.state.latitude,
                this.state.longitude]}
            /> 

            <GeoJSON style={this.countryStyle} data={this.state.geoJson} />
            

            {/* <Popup>
              <span>
                <h4>{this.props.searcLocation}</h4>
              </span>
            </Popup> */}
           
        </MapContainer>
        </div>
        
                    
        )
    }
}

export default MyMap;
