import React from 'react';
import './currentInfo.css';
import Total from '../../utils/totals';

class CurrentInfo extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            currentInfections: 0,
            currentDeaths: 0
        }
        this.findTotal = this.findTotal.bind(this)
        
    }

    findTotal(){
        Total.search().then(data => {
            this.setState({
                currentInfections: data.totalInfections,
                currentDeaths: data.totalDeaths
        })
        })
    }

    componentDidMount() {
        window.onload = this.findTotal()
    }


    render(){
        return(
            <div className="pr-4 pt-2">
                <p>Total infections in UK: {this.state.currentInfections}</p>
                <p>Total deaths in UK: {this.state.currentDeaths}</p>
                <p>New infections today: </p>
                <p>New deaths today: </p>
            </div>
        )
    }
}


export default CurrentInfo;
