import React from 'react';
import './currentInfo.css';
import axios from 'axios';
import CountUp from 'react-countup';

class CurrentInfo extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            totalInfections: 0,
            totalDeaths: 0,
            newCases: 0,
            newDeaths: 0,
            countup: 0
        }
        this.findTotal = this.findTotal.bind(this)
        
    }


    findTotal = async () => {
        const response = await axios.get('http://localhost:5000/national')
        if (response.status !== 200){
            throw Error(response.message)
        } else {
            console.log(response)
            this.setState({ totalDeaths: response.data.data[0].deathsCumulative, totalInfections: response.data.data[0].casesCumulative, newCases: response.data.data[0].casesDaily, newDeaths: response.data.data[0].deathsDaily})
        }
    }

    componentDidMount() {
        window.onload = this.findTotal()
    }

    render(){
        return(
            <div className="pr-4 pt-2">
                <p>Total infections in UK: <CountUp end={this.state.totalInfections} duration={0.5}/></p>
                <p>Total deaths in UK: <CountUp end={this.state.totalDeaths} duration={0.5}/></p>
                <p>New infections today: <CountUp end={this.state.newCases} duration={0.5}/> </p>
                <p>New deaths today: <CountUp end={this.state.newDeaths} duration={0.5}/></p>
            </div>
        )
    }
}


export default CurrentInfo;
