import './modal.css';

const Modal = ({ handleClose, show, children }) => {
    const showHideClassName = show ? "modal display-block" : "modal display-none";
    
    return (
        <div className={showHideClassName}>
            <section className="modal-main">
                <h3>National Statistics</h3><br></br>
                <p className="nationalStat">Total infections: <span>{children.totalInfections}</span></p>
                <p className="nationalStat">Total deaths: <span>{children.totalDeaths}</span></p>
                <p className="nationalStat">New infections: <span>{children.newCases}</span></p>
                <p className="nationalStat">New deaths: {children.newDeaths}</p>
                <p className="nationalStat">Total first vaccinations: {children.cumFirstVaccinations}</p>
                <p className="nationalStat">Total second vaccinations: {children.cumSecondVaccinations}</p>
                <p className="nationalStat">First vaccinations reveived on {(new Date(Date.now() - 86400000)).toISOString().split("T")[0]}: {children.firstVaccinationsDaily}</p>
                <p className="nationalStat">Second vaccinations received on {(new Date(Date.now() - 86400000)).toISOString().split("T")[0]}: {children.secondVaccinationsDaily}</p>

                
                <br></br><br></br>
                <button type="button" onClick={handleClose}>Close</button>
            </section>
        </div>
    )
}

export default Modal;
