import React, { createRef, useState, useRef } from 'react';
import { MapContainer, TileLayer, GeoJSON } from "react-leaflet";
import axios from 'axios';

import Card1 from '../Card/card';


function Map() {

  const [locationVal, setLocation] = useState('');
  const [coords, setCoords] = useState([53.9600, 1.0873]);
  const [geoJson, setGeoJson] = useState(null);
  const [key, setKey] = useState(0)
       
     const searchLocation = async (location) => {
        setLocation(location)
        const response = await axios.get('http://localhost:5000/location', { params: {location} })
        if (response.status !== 200){
            throw Error(response.message)
        } else {
            setGeoJson(response.data.geoJson)
            setKey(response.data.key)
            } 
        } 
      
     const  countryStyle = {
          fillColor: 'red'
      };

      const mapRef = useRef();
      const handleOnSetView = () => {
        console.log('fitview')
        const { current = {} } = mapRef;
        const { leafletElement: map } = current;
        const bounds = geoJson.getBounds()
        map.setView(bounds, 14);
      }
         
        const ReturnGeoJson = () => {   
          handleOnSetView()       
          return geoJson && (
            <GeoJSON data={geoJson} key={key}
            />
          )
      }

        return(
        <div>
      <MapContainer zoomControl={false} center={[51.5074, .1278]} zoom={7.5}>
            <Card1 searchLocation={searchLocation} location={locationVal}/>
            <TileLayer
            url="https://api.maptiler.com/maps/uk-openzoomstack-road/256/{z}/{x}/{y}.png?key=X7qpq0iR5R376h5yMZXo" 
            attribution='<a href="https://www.maptiler.com/copyright/" target="_blank">&copy; MapTiler</a> <a href="https://www.openstreetmap.org/copyright" target="_blank">&copy; OpenStreetMap contributors</a>'
            />


            {ReturnGeoJson()}
           
        </MapContainer>
        </div>
        
                    
   )
}

export default Map;
