import React from 'react';
import './searchBar.css';


class SearchBar extends React.Component{
    constructor(props){
        super(props);
        this.state = {
           area: '' 
        }
        
        this.handleLocationChange = this.handleLocationChange.bind(this);
        this.handleSearch = this.handleSearch.bind(this);

    }

handleLocationChange(e){
    this.setState({ area: e.target.value });
}



handleSearch(e){
    this.props.searchLocation(this.state.area);
    let area = this.state.area;
    if (!area || !isNaN(area)) {
        alert("Please enter a valid location")
    } else {
        this.props.searchLocation(area);
    e.preventDefault();
    }
}

    render(){
        return(
            
            <div>
                
                <label for="searchBar">
                    <select onChange={this.handleLocationChange}type="text" placeholder=""><option>Please enter an area</option></select>
                    {/* <button onClick={this.handleSearch} className="btn btn-success">Submit</button> */}
                </label>
            </div>
            
        )
    }
}

export default SearchBar;
