// import { divIcon } from 'leaflet';
import React, { createRef } from 'react';
import { MapContainer, TileLayer, GeoJSON, useMap } from "react-leaflet";
import axios from 'axios';

import Card1 from '../Card/card';


class MyMap extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            location: '',
            latitude: 52.5,
            longitude: 0.12,
            geoJson: null,
            // key
        }
        this.searchLocation = this.searchLocation.bind(this);
        // this.clickLocation = this.clickLocation.bind(this);
        this.handleClick = this.handleClick.bind(this);
        this.mapRef = createRef();
      }
      
      searchLocation = async (location) => {
        this.setState({
          location: location})
        const response = await axios.get('http://localhost:5000/location', { params: {location} })
        if (response.status !== 200){
            throw Error(response.message)
        } else {
            this.setState({ geoJson: response.data.geoJson, key: response.data.key })
        }
      }

      handleClick() {
        //  const map = useMap()
        //  map.fitBounds(this.state.latitude, this.state.longitude)
      }

      countryStyle = {
          fillColor: 'red'
      };
      
       
    render(){    

        const ReturnGeoJson = () => {
        let map = this.mapRef
        // const layer = customAreaRef.current.leafletElement
        if(this.state.geoJson){
          let bounds = this.state.geoJson.getBounds();
          map.fitBounds(bounds, {
            animate: true
          })
        }
  
          return this.state.geoJson && (
            <GeoJSON data={this.state.geoJson} key={this.state.key}
            />
          )
      }

        return(
        <div>
      <MapContainer zoomControl={false} center={[this.state.latitude, this.state.longitude]} zoom={5.5}>
            <Card1 searchLocation={this.searchLocation} location={this.state.location}/>
            <TileLayer
            url="https://api.maptiler.com/maps/uk-openzoomstack-road/256/{z}/{x}/{y}.png?key=X7qpq0iR5R376h5yMZXo" 
            attribution='<a href="https://www.maptiler.com/copyright/" target="_blank">&copy; MapTiler</a> <a href="https://www.openstreetmap.org/copyright" target="_blank">&copy; OpenStreetMap contributors</a>'
            />
            {/* <TileLayer fitBounds={[this.state.latitude, this.state.longitude]} /> */}


            {ReturnGeoJson()}
           
        </MapContainer>
        </div>
        
                    
        )
    }
}

export default MyMap;
